import React, { useState, useEffect } from 'react'
import axios from 'axios'
import './Routines.css'

async function getRoutines()  {
    let { data } = await axios.get('https://fitnesstrac-kr.herokuapp.com/api/routines')

    // display routines respone object in console
    console.log(data)
    return data
}

const Routines = () => {
    const [ routines, setRoutines] = useState([])

    useEffect(async () => {
        let data = await getRoutines();
        console.log('Running useEffect')
        setRoutines(data)
    }, [])

    console.log(routines);


    let displayRoutines = routines.map((r, idx) => {
       return <SingleRoutine key={idx} routine={r} />
    })
    return <div className='routines'>{displayRoutines}</div>
}

const SingleRoutine = ({ routine }) => {
    const activities = routine.activities;
    let displayActivities = activities.map((a, idx) => {
        return <Activity key={idx} activity={a} />
     })

    return <div className = 'Routine'>
            <h1>Routine Name: {routine.name}</h1>
            <h2>Creator Name: {routine.creatorName}</h2>
            <h3>Goal: {routine.goal}</h3>
            <ul>{displayActivities}</ul>
    </div>
}

const Activity = ({activity}) => {
    return <li className='activity'>
        <h3>{activity.name}</h3>
        <h3>{activity.description}</h3>
    </li>
}

export default Routines;
