import React, {useState, useEffect} from 'react';
import axios from 'axios';
import './Activities.css';
import { getActivities } from '../api/index';

const Activities = () => {
    const [activities, setActivities] = useState([])

    useEffect(async () => {
        let data = await getActivities();
        setActivities(data);
    }, []);

    let displayActivities = activities.map((a, idx) => {
        return <SingleActivity key={idx} activity={a} />
    })
    return <div className='activity'>{displayActivity}</div>
}

const Activity = ({activity}) => {
    return <li className='activity'>
        <h3>Activity Name: {activity.name}</h3>
        <h3>Activity Description: {activity.description}</h3>
        <br></br>
    </li>
}

export default Activities;

